use serde::Deserialize;
use url::Url;

use crate::{
    marketplace::item::{Amount, GeoLocation},
    prelude::*,
};

#[derive(Debug, Deserialize)]
pub struct Listings {
    #[serde(rename = "listings")]
    pub inner: Vec<Listing>,
}

impl IntoIterator for Listings {
    type Item = Listing;
    type IntoIter = <Vec<Listing> as IntoIterator>::IntoIter;

    fn into_iter(self) -> Self::IntoIter {
        self.inner.into_iter()
    }
}

#[derive(Debug, Deserialize)]
pub struct Listing {
    /// Marktplaats item ID, looks like `m2137081815`.
    #[serde(rename = "itemId")]
    pub item_id: String,

    /// Advertisement title.
    pub title: String,

    /// Advertisement description body.
    pub description: String,

    #[serde(rename = "categorySpecificDescription", default)]
    pub category_specific_description: Option<String>,

    /// Variable quality images.
    #[serde(default, rename = "pictures")]
    pub pictures: Vec<Picture>,

    #[serde(rename = "priceInfo")]
    pub price: Price,

    /// Advertisement URL path without schema and host.
    #[serde(rename = "vipUrl")]
    pub url_path: String,

    #[serde(rename = "sellerInformation")]
    pub seller: Seller,

    pub location: Location,

    #[serde(rename = "extendedAttributes", default)]
    pub extended_attributes: Vec<ExtendedAttribute>,
}

impl Listing {
    pub fn brand(&self) -> Option<&str> {
        self.extended_attributes.iter().find_map(ExtendedAttribute::as_brand)
    }
}

#[derive(Debug, Deserialize)]
pub struct Seller {
    #[serde(rename = "sellerId")]
    pub id: u32,

    #[serde(rename = "sellerName")]
    pub name: String,
}

impl TryFrom<Seller> for crate::marketplace::item::Seller {
    type Error = Error;

    fn try_from(seller: Seller) -> Result<Self> {
        let profile_url =
            Url::parse(&format!("https://www.marktplaats.nl/u/{}/{}/", seller.name, seller.id))?;
        Ok(Self::builder().username(seller.name).profile_url(profile_url).build())
    }
}

#[derive(Deserialize, Debug, Eq, PartialEq)]
#[serde(tag = "priceType")]
pub enum Price {
    /// Fixed price, bids are not allowed.
    #[serde(rename = "FIXED")]
    Fixed {
        #[serde(rename = "priceCents", deserialize_with = "Amount::deserialize_from_cents")]
        asking: Amount,
    },

    #[serde(rename = "ON_REQUEST")]
    OnRequest,

    /// Bids are allowed. The minimal bidding price is not available in the listing.
    #[serde(rename = "MIN_BID")]
    MinimalBid {
        /// Asking price.
        #[serde(rename = "priceCents", deserialize_with = "Amount::deserialize_from_cents")]
        asking: Amount,
    },

    #[serde(rename = "SEE_DESCRIPTION")]
    SeeDescription,

    #[serde(rename = "NOTK")]
    ToBeAgreed,

    #[serde(rename = "RESERVED")]
    Reserved,

    /// No asking price, only bidding.
    #[serde(rename = "FAST_BID")]
    FastBid,

    #[serde(rename = "FREE")]
    Free,

    #[serde(rename = "EXCHANGE")]
    Exchange,
}

impl From<Price> for crate::marketplace::item::Price {
    fn from(price: Price) -> Self {
        match price {
            Price::Fixed { asking } => Self::Fixed(asking),
            Price::OnRequest => Self::OnRequest,
            Price::MinimalBid { asking } => Self::MinimalBid(asking),
            Price::SeeDescription => Self::SeeDescription,
            Price::ToBeAgreed => Self::ToBeAgreed,
            Price::Reserved => Self::Reserved,
            Price::FastBid => Self::FastBid,
            Price::Free => Self::Fixed(Amount::ZERO),
            Price::Exchange => Self::Exchange,
        }
    }
}

#[derive(Clone, Debug, Deserialize)]
#[expect(clippy::struct_field_names)]
pub struct Picture {
    #[serde(rename = "extraExtraLargeUrl", default)]
    pub extra_large_url: Option<String>,

    #[serde(rename = "largeUrl", default)]
    pub large_url: Option<String>,

    #[serde(rename = "mediumUrl", default)]
    pub medium_url: Option<String>,
}

impl TryFrom<&Picture> for Option<Url> {
    type Error = Error;

    fn try_from(picture: &Picture) -> Result<Self> {
        picture
            .extra_large_url
            .as_deref()
            .or(picture.large_url.as_deref())
            .or(picture.medium_url.as_deref())
            .map_or(Ok(None), |url| {
                Url::parse(url).context("failed to parse the picture URL").map(Some)
            })
    }
}

#[derive(Debug, Deserialize)]
pub struct Location {
    #[serde(rename = "cityName")]
    pub city_name: Option<String>,

    #[serde(default)]
    pub latitude: Option<f64>,

    #[serde(default)]
    pub longitude: Option<f64>,
}

impl From<Location> for Option<crate::marketplace::item::Location> {
    fn from(location: Location) -> Self {
        match location.city_name {
            Some(toponym) => {
                let geo = if let (Some(latitude), Some(longitude)) =
                    (location.latitude, location.longitude)
                {
                    Some(GeoLocation::builder().latitude(latitude).longitude(longitude).build())
                } else {
                    None
                };
                let this = crate::marketplace::item::Location::builder()
                    .toponym(toponym)
                    .maybe_geo(geo)
                    .build();
                Some(this)
            }
            None => None,
        }
    }
}

#[derive(Debug, Deserialize)]
#[serde(tag = "key", content = "value")]
pub enum ExtendedAttribute {
    #[serde(rename = "condition")]
    Condition(Condition),

    #[serde(rename = "delivery")]
    Delivery(Delivery),

    #[serde(rename = "brand")]
    Brand(String),

    #[serde(untagged)]
    #[expect(dead_code)]
    Other(OtherAttribute),
}

impl ExtendedAttribute {
    pub const fn as_condition(&self) -> Option<Condition> {
        match self {
            Self::Condition(condition) => Some(*condition),
            _ => None,
        }
    }

    pub const fn as_delivery(&self) -> Option<Delivery> {
        match self {
            Self::Delivery(delivery) => Some(*delivery),
            _ => None,
        }
    }

    pub const fn as_brand(&self) -> Option<&str> {
        match self {
            Self::Brand(brand) => Some(brand.as_str()),
            _ => None,
        }
    }
}

#[derive(Copy, Clone, Debug, Deserialize)]
pub enum Condition {
    #[serde(rename = "Nieuw")]
    New,

    #[serde(rename = "Zo goed als nieuw")]
    AsGoodAsNew,

    #[serde(rename = "Gebruikt")]
    Used,

    #[serde(rename = "Niet werkend")]
    NotWorking,

    Refurbished,
}

impl From<Condition> for crate::marketplace::item::Condition {
    fn from(condition: Condition) -> Self {
        match condition {
            Condition::New => Self::New(crate::marketplace::item::New::Unspecified),
            Condition::AsGoodAsNew => Self::New(crate::marketplace::item::New::AsGood),
            Condition::Used => Self::Used(crate::marketplace::item::Used::Unspecified),
            Condition::NotWorking => Self::Used(crate::marketplace::item::Used::NotFullyFunctional),
            Condition::Refurbished => Self::Refurbished,
        }
    }
}

#[derive(Copy, Clone, Debug, Deserialize)]
pub enum Delivery {
    #[serde(rename = "Ophalen")]
    CollectionOnly,

    #[serde(rename = "Verzenden")]
    ShippingOnly,

    #[serde(rename = "Ophalen of Verzenden")]
    CollectionOrShipping,
}

impl From<Delivery> for crate::marketplace::item::Delivery {
    fn from(delivery: Delivery) -> Self {
        match delivery {
            Delivery::CollectionOnly => Self::CollectionOnly,
            Delivery::ShippingOnly => Self::ShippingOnly,
            Delivery::CollectionOrShipping => Self::Both,
        }
    }
}

#[derive(Debug, Deserialize)]
#[expect(dead_code)]
pub struct OtherAttribute {
    pub key: String,
    pub value: String,
}

impl TryFrom<Listing> for crate::marketplace::item::Item {
    type Error = Error;

    fn try_from(listing: Listing) -> Result<Self> {
        let condition =
            listing.extended_attributes.iter().find_map(ExtendedAttribute::as_condition);
        let delivery = listing.extended_attributes.iter().find_map(ExtendedAttribute::as_delivery);
        let picture_url = if let Some(picture) = listing.pictures.first() {
            Option::<Url>::try_from(picture)?
        } else {
            None
        };
        Ok(Self::builder()
            .id(listing.item_id)
            .url(Url::parse(&format!("https://www.marktplaats.nl{}", listing.url_path))?)
            .title(listing.title)
            .description(listing.category_specific_description.unwrap_or(listing.description))
            .maybe_condition(condition.map(Into::into))
            .maybe_delivery(delivery.map(Into::into))
            .price(listing.price.into())
            .seller(listing.seller.try_into()?)
            .maybe_location(listing.location.into())
            .maybe_picture_url(picture_url)
            .build())
    }
}

